uuidv4();

class ShoppingList extends Component
{
    state = {
        items: [
            { id: uuidv4(), name:"egg" },
            { id: uuidv4(), name:"milk" },
            { id: uuidv4(), name:"meat" },
            { id: uuidv4(), name:"proteins" }

        ]
    }









    render()
    {
        const { items } = this.state
        return(
            <Container>
                <Button
